// Pie chart
var PieChart = function() {
    var $chart = $("#chart-pie");

    if ($chart.length) {
        (function($) {
            var pieChart = new Chart($chart, {
                type: "pie",
                data: {
                    labels: ["Category A", "Category B", "Category C", "Category D"],
                    datasets: [{
                        data: [30, 20, 15, 35],
                        backgroundColor: ["#FF6384", "#36A2EB", "#FFCE56", "#4CAF50"]
                    }]
                }
            });
            $chart.data("chart", pieChart);
        })($);
    }
}();
